#!/bin/bash

/sbin/ifconfig | sed -e 's/^$/---------------------------/' | sed -r -e 's/^(.*addr:([0-9]+\.){3})[0-9]{3}/\1xxx/' | sed -r -e 's/^(.*addr:([0-9]+\.){3})[0-9]{2}/\1xx/' | sed -r -e 's/^(.*addr:([0-9]+\.){3})[0-9]{1}/\1x/' | sed -r -e 's/^(.*addr:([0-9]+\.){2})([0-9]{3})(\.x+)/\1xxx\4/' | sed -r -e 's/^(.*addr:([0-9]+\.){2})([0-9]{2})(\.x+)/\1xx\4/' | sed -r -e 's/^(.*addr:([0-9]+\.){2})([0-9]{1})(\.[x]+)/\1x\4/' | sed -r -e 's/^(.*addr:([0-9]+\.){1})([0-9]{3})((\.x+){2})/\1xxx\4/' | sed -r -e 's/^(.*addr:([0-9]+\.){1})([0-9]{2})((\.x+){2})/\1xx\4/' | sed -r -e 's/^(.*addr:([0-9]+\.){1})([0-9]{1})((\.[x]+){2})/\1x\4/' | sed -r -e 's/^(.*addr:)([0-9]{3})((\.x+){3})/\1xxx\3/' | sed -r -e 's/^(.*addr:)([0-9]{2})((\.x+){3})/\1xx\3/' | sed -r -e 's/^(.*addr:)([0-9]{1})((\.[x]+){2})/\1x\3/'
